INSERT INTO dbo.AdfOrchestrationRunLog
(
    RunId,
    OrchestrationName,
    PipelineName,
    StepName,
    TableSchema,
    TableName,
    Status,
    StartTimeUtc,
    RangeStartUtc,
    RangeEndUtc,
    CorrelationId
)
VALUES
(
    @RunId,
    'Backup',
    'PL_BackupToParquet',
    'Backup',
    @TableSchema,
    @TableName,
    'Running',
    SYSUTCDATETIME(),
    @RangeStartUtc,
    @RangeEndUtc,
    @CorrelationId
);
